function currentDateFormatted(localTime) {
  const date = new Date(localTime);

  const options = {
    weekday: "long",
    day: "numeric",
    month: "long",
    year: "numeric",
  };
  const formattedDate = date.toLocaleDateString("en-Us", options);
  return formattedDate;
}

function currentTimeFormatted(localTime) {
  const date = new Date(localTime);

  const options = {
    hour: "numeric",
    minute: "numeric",
    hour12: true,
  };

  const formattedTime = date.toLocaleTimeString("en-US", options).toLowerCase();
  return formattedTime;
}

function getAirQuality(index) {
  switch (index) {
    case 1:
      return "Good";
    case 2:
      return "Moderate";
    case 3:
      return "Unhealthy";
    case 4:
      return "Unhealthy";
    case 5:
      return "Very Unhealthy";
    case 6:
      return "Hazardous";
  }
}

function getDay(date) {
  const newDate = new Date(date);
  const dayName = newDate.toLocaleString("en-US", { weekday: "long" });
  return dayName;
}

